  $( document ).ready( function(){

    //검색조건값을 초기 셋팅합니다. 
    $('input:radio[name="_srch_options1"]').each(function() {
        if(this.value=='${pmap._srch_options1}$'){ 
            this.checked = true; 
            $(this).parent().addClass('active');
        }
    });

    $('#_srch_op1_date1').val('${pmap._srch_op1_date1}$')
    $('#_srch_op1_date2').val('${pmap._srch_op1_date2}$')

    $('input:radio[name="_srch_options2"]').each(function() {
        if(this.value=='${pmap._srch_options2}$'){ 
            this.checked = true; 
            $(this).parent().addClass('active');
        }
    });
    $('#_srch_op2_txt').val('${pmap._srch_op2_txt}$')

    $('input:radio[name="_srch_options3"]').each(function() {
        if(this.value=='${pmap._srch_options3}$'){ 
            this.checked = true; 
            $(this).parent().addClass('active');
        }
    });
    //조건검색 초기 셋팅 끝

    //조건 검색
    $('#btn_srch_info').click(function() {

      //조건1 validation:  평가신청일자,실사배정일자일 경우 날짜 2개 중에 하나는 있어야 함
      var o1 = $('input:radio[name="_srch_options1"]:checked').val();
      if(o1 == 'op1_02'||o1 == 'op1_03'){
        if($('#_srch_op1_date1').val()=='' && $('#_srch_op1_date2').val()==''){
          alert('평가신청일자나 실사배정일자를 선택한 경우 검색 날짜를 입력해 주셔야 합니다.');
          return;
        }
      }else{
        //일자검색이 아닌 경우 날짜 값을 공백으로 바꾸어 준다.
        $('#_srch_op1_date1').val('');
        $('#_srch_op1_date2').val('');
      }

      //조건2 validation
      var o2 = $('input:radio[name="_srch_options2"]:checked').val();
      if(o2 != undefined && o2 != 'op2_01'){
        if($('#_srch_op2_txt').val()=='' ){
          alert('사업자번호,기업명,은행명,실사자 선택시 해당 검색어를 입력해 주셔야합니다.');
          return;
        }
      }else{
       $('#_srch_op2_txt').val('');
      }

      //조건3 validation
      //조건없음            


      $('#frm_srch_info').attr('action','?');
      $('#frm_srch_info').submit();
    });

    // 엑셀 다운로드
    $('#btn_excel_down').click(function() {
      $('#frm_exl').attr('action','/admin_status_excel.do?_resp=excel');
      $('#frm_exl').submit();
    });


    //배정취소
    $('#assign_canc').click(function() {
           var  s_no =$('#m_s_no').val();
           if(confirm('정말로 취소 하시겠습니까? 취소시 배정정보가 삭제됩니다')){
           }else{
             return;
           }

           var status = $('#m_inq_status'+s_no).val();

           ${if(pmap.scr_typ==grp_master)}$
           if(status=='0' || status=='3' || status=='4'){
           ${else}$
           if( status=='3' || status=='4'){
           ${endif}$
           	 if(status=='0'){
           	 	alert('귀사에 배정된 실사를 취소할 수 없습니다. 실사자를 배정해주세요.');
           	 } else{
             	alert('실사 진행, 완료에서는 배정취소가 되지 않습니다.');
             }
             return;
           }

           $.ajax({
               cache: false,
               type: "get",
               url: "/assign_canc.ado",
               ${if(pmap.scr_typ==grp_master)}$
               data: 's_no='+s_no+"&typ=grp_master",
               ${else}$
               data: 's_no='+s_no+"&typ=admin",
               ${endif}$
               dataType: 'json',
               success: function(json) {
                 alert(json.msg);
                 $('#modal-assigner').modal('hide');
                 self.location.reload();
               },
               error: function(jqXHR, textStatus, errorThrown) {
                 console.error('ERRORS: ' + textStatus);
                 alert('ERRORS: ' + textStatus);
               }
          });
    });

  });  

  //실사자 정보 모달 
  function mem_info(s_no){
    var col = new Array("m_user_nm","m_grp_nm","m_user_nm","m_tel","m_cell","m_email","m_addr");
    for(i=0 ; i<col.length; i++){
      $('#'+col[i]).html($('#'+col[i]+s_no).val());
    }
    $('#m_user_id').val($('#m_user_id'+s_no).val());
    $('#m_s_no').val($('#m_s_no'+s_no).val());
  }


  //완료 보고서 조회 기능
  function getRptNo(no){
  	var s_n = $('#m_s_no'+no).val();
  	var status = $('#m_inq_status'+s_n).val();
  	var actual_id = $('#m_user_id'+s_n).val();
  	if (status != '4'){
    	alert('완료되지 않은 보고서는 조회할 수 없습니다.');
    } else{
      var pop_url ="/rpt_view.do?s_no="+s_n +"&typ=inq_rpt";
    	popup_open_new(pop_url, 'rpt_pop', '900', '800','0');
    }
  }

  //리포트 팝업 생성
  function popup_open_new( url, name, iwidth, iheight, left, top  ){ 
     popWindowNew  =  window.open( url,name ,'scrollbars=yes,resizable=yes,width=' + iwidth + ',height=' + iheight+',left='+left+',top='+top);                       
  }




