//정보수정과 신규등록 모달 구분
var btnT='';
  $(document).ready(function(){

    //신규등록 & 정보수정 모달의 등록완료 버튼 클릭시, 
    $('#btnNewReg').on('click', function(){
      ${if(scr_typ==grp_master)}$
        $('input[name="scr_typ"]').attr('value','grp_master');
      ${else}$
        $('input[name="scr_typ"]').attr('value','');
      ${endif}$

      $('input[name="btn_type"]').attr('value', btnT);    
      var pw = $('#user_pw').val(); var pwChk = $('#user_pwChk').val();
      if (pw != pwChk){
        alert('비밀번호가 일치하지 않습니다.');
      }else{

        $.ajax({
             type: "POST",
             url: "/admin_mem_reg_act.ado",
              data: $('#frm_mem_reg').serialize() ,
             dataType: 'json',
             success: function(json) {
               alert(json.msg);
               $('#user_id').val(""); $('#user_nm').val(""); $('#user_pw').val(""); $('#user_pwChk').val("");
                 $('#tel').val(""); $('#cell').val(""); $('#email').val(""); $('#addr').val("");
                 self.location.reload();
             },
             error: function(jqXHR, textStatus, errorThrown) {
               console.error('ERRORS: ' + textStatus);
               alert('ERRORS: ' + textStatus);
             }
        });
      }
    });

    //신규등록 버튼
    $('#btnNewReg_').on('click', function(){
      $('#modal-new-register-label').text('실사자 신규등록');
    });

    //정보수정 버튼
    $('#btnInfoMod').on('click', function(){
      $('input[name="btn_type"]').attr('value', btnT);    
      var user_id = '';
      var checkedSize = $('input[name=in_chkbox]:checked').size();
      if (checkedSize < 1){
        alert('수정할 실사자를 선택해주세요.');
        return;
      } else if (checkedSize > 1){
        alert('한명만 선택해주세요.');
        return;
      } else{
        $("input[name='in_chkbox']").each(
          function(){
            if (this.checked){
              user_id = this.value;
            } 
          }
        );
        $('#modal-new-register').modal('show');
        $('#modal-new-register-label').text('실사자 정보수정');
      }
        $('#grp_nm').text( $('#m_grp_nm' + user_id).val() );
         $('#user_id').val(user_id); $('#user_id').prop("readonly", true);  $('#user_nm').val($('#m_user_nm' + user_id).val()); 
         $('#user_pw').prop("readonly", true); $('#user_pwChk').prop("readonly", true);
         $('#tel').val($('#m_tel' + user_id).val()); $('#cell').val($('#m_cell' + user_id).val()); 
         $('#email').val($('#m_email' + user_id).val()); $('#addr').val($('#m_addr' + user_id).val());


    });
    $('#btn_cancel').on('click', function(){
       $('#user_id').prop("readonly", false); $('#user_pw').prop("readonly", false); $('#user_pwChk').prop("readonly", false);
       $('#user_id').val(""); $('#user_nm').val(""); $('#user_pw').val(""); $('#user_pwChk').val("");
       $('#tel').val(""); $('#cell').val(""); $('#email').val(""); $('#addr').val("");
    });

    //실사자 삭제
    $('#btn_del').on('click', function(){
      var checkedId = $('input[name=in_chkbox]:checked').map(function(){
      return $(this).val();
      }).get();
      if (checkedId == ''){
        alert('삭제할 실사자를 선택해주세요');
      } else {

        if(confirm('정말로 삭제 하시겠습니까? 삭제시 해당 실사자정보는 삭제됩니다')){
           }else{ return; }

            $.ajax({
             type: "POST",
             url: "/admin_mem_del_act.ado",
               data: {checkedId : JSON.stringify(checkedId)},
             dataType: 'json',
             success: function(json) {
               alert(json.msg);
                 self.location.reload();
             },
             error: function(jqXHR, textStatus, errorThrown) {
               console.error('ERRORS: ' + textStatus);
               alert('ERRORS: ' + textStatus);
             }
        });
      }

    });

  });



