  var now_s_no ='';  //배정하기 모달을 띄웠을때 선택되어진 S_NO를 저장하기 위함
  function setv(s_no, addr){
 	now_s_no= s_no; 
 	$('#cmp_addr').text(addr); //모달의 상세정보 창
 	$('#typ').val('default');
 	srch();
 } 

  //실사자 검색함수
 function srch(){
 	//조건에 따라 검색한 실사자 테이블.
       var html ='<table class="table table-info">';
				html += '<thead>';
				html += '<tr>';
				html += '	<th>선택</th>';
				html += '	<th>성명</th>';
				html += '	<th>연락처</th>';
				html += '	<th>이메일</th>';
				html += '	<th>주소</th>';
				html += '</tr>';
				html += '</thead>';
				html += '<tbody>';      
    	 $.ajax({
             type: "POST",
              url: "/srch_actual.ado",
              data: $('#frm_srch').serialize(),
             dataType: 'json',
             success: function(json) {
               	$.each(json.list, function(key, value){
               		html += '	<tr>';
					html += '		<td><input type="radio" id="assign_rdo" name="assign_rdo" value="' + value.user_id + '|'+value.user_nm+'"></td>';
					html += '		<td>'+value.user_nm+'</td>';
					html += '		<td>'+value.cell+'</td>';
					html += '		<td><a href="mailto:'+value.email+'">'+value.email+'</a></td>';
					html += '		<td>'+value.addr+'</td>';
					html += '	</tr>';
               	});
               	html += '</tbody>';
				html += '</table>';

				$('#default_table').html(html);
				$('#srch_opt_txt').val('');
				$('#typ').val('default');
             },
             error: function(jqXHR, textStatus, errorThrown) {
               console.error('ERRORS: ' + textStatus);
               alert('ERRORS: ' + textStatus);
             }
        });


 }

  $( document ).ready( function(){
    // 엑셀 다운로드
    $('#btn_excel_down').click(function() {
      $('#frm_exl').attr('action','/grp_master_main_excel.do?_resp=excel');
      $('#frm_exl').submit();
    });

    //조건 검색
    $('#btn_srch_actual').click(function() {
    	$('#typ').val('');
    	srch();

    });

	     //담당자 지정 하기
	  $('#go_assign_mem').on('click', function(){
	     var ass_info = $('input:radio[name="assign_rdo"]:checked').val();
	     //split로 Userid,와 user_nm 나누기 자바스크립트 찾아볼것 
	     var dataArr = ass_info.split('|');
	     $.ajax({
	         cache: false,
	         type: "get",
	         url: "/grp_mem_assign_act.ado",
	         data: 'user_id='+dataArr[0]+'&s_no='+now_s_no,
	         dataType: 'json',
	         success: function(json) {
	           alert(json.msg);
	           //선택되어진 inputbox에 배정자 명 SET하기
	           $('#ass_'+now_s_no).val(dataArr[1]);
	           $('#modal-assign-list').modal('hide');
	           self.location.reload();
	         },
	         error: function(jqXHR, textStatus, errorThrown) {
	           console.error('ERRORS: ' + textStatus);
	           alert('ERRORS: ' + textStatus);
	         }
	    });
	  });
  });



