var  now_s_no ='';  <!-- 실사자 배정 확인을 위한 것 -->
  function setv(s_no){now_s_no= s_no; btn_act_chk();}


  function btn_act_chk(){
    var inqDate = $('#inqExpDate_'+now_s_no).val() + ":00";

    if (inqDate == '' || inqDate.length != 19){
      alert('실사예정일을 정확히 입력하세요');
    } else{
       $.ajax({
           cache: false,
           type: "get",
           url: "/act_chk_proc.ado",
           data: 'inq_due_date='+ inqDate +'&s_no='+now_s_no,
           dataType: 'json',
           success: function(json) {
             alert(json.msg);
             self.location.reload();
             },
           error: function(jqXHR, textStatus, errorThrown) {
             console.error('ERRORS: ' + textStatus);
             alert('ERRORS: ' + textStatus);
           }
      });
    }
  }

