var now_s_no = "";
function setv(s_no){ now_s_no = s_no; actDateSet();}

//실사예정일 변경 
function setDue(s_no){ now_s_no = s_no; actDueSet();}

function actDueSet(){
      var dueDate = $('#dueDate_'+now_s_no).val() + ":00";

      if(dueDate == "" || dueDate.length != 19){
        alert('실사예정일을 정확히 입력하세요.');
      }else{
        $.ajax({
               cache: false,
             type: "get",
             url: "/due_dateset_proc.ado",
             data: 'inq_due_date='+ dueDate +'&s_no='+now_s_no,
             dataType: 'json',
             success: function(json) {
              alert(json.msg);
              self.location.reload();
             },
             error: function(jqXHR, textStatus, errorThrown) {
                 console.error('ERRORS: ' + textStatus);
                 alert('ERRORS: ' + textStatus);
              }
          });
      }

}

function actDateSet(){
      var actDate = $('#actDate_'+now_s_no).val() + ":00";

      if(actDate == "" || actDate.length != 19){
        alert('실사일을 정확히 입력하세요.');
      }else{
        $.ajax({
               cache: false,
             type: "get",
             url: "/act_dateset_proc.ado",
             data: 'inq_act_date='+ actDate +'&s_no='+now_s_no,
             dataType: 'json',
             success: function(json) {
              alert(json.msg);
              self.location.reload();
             },
             error: function(jqXHR, textStatus, errorThrown) {
                 console.error('ERRORS: ' + textStatus);
                 alert('ERRORS: ' + textStatus);
              }
          });
      }

}
  //보고서 작성시 클릭 이벤트 처리
  function go_rpt(s_no){
    //실사일 값이 없으면 alert 처리
    if($('#actDate_'+s_no).val()==''){
      alert('실사일을 먼저 입력해 주세요');
      return;
    }
    self.location.href="/go_report.do?s_no="+s_no;


  }

  $(document).ready(function(){
      //검색조건값을 초기 셋팅합니다. 
      $('input:radio[name="_srch_options1"]').each(function() {
          if(this.value=='${pmap._srch_options1}$'){ 
              this.checked = true; 
              $(this).parent().addClass('active');
          }
      });
       $('#_srch_op1_txt').val('${pmap._srch_op1_txt}$')

       $('input:radio[name="_srch_options2"]').each(function() {
          if(this.value=='${pmap._srch_options2}$'){ 
              this.checked = true; 
              $(this).parent().addClass('active');
          }
      });

       //조건 검색
      $('#btn_srch_info').click(function() {
        var o1 = $('input:radio[name="_srch_options1"]:checked').val();
          if(o1 != undefined && o1 != 'op1_01'){
             if($('#_srch_op1_txt').val()=='' ){
            alert('사업자번호나 기업명을 선택한 경우 해당 검색어를 입력해 주셔야 합니다.');
            return;
             }

         } else {
          $('#_srch_op1_txt').val('');
         }

        $('#frm_srch_info').attr('action','?');
          $('#frm_srch_info').submit();
      });


      $('input:text[name="dueDate"]').each(function(){
		  $(this).appendDtpicker({
			"autodateOnStart": false
		  });
	  });

	   $('input:text[name="actDate"]').each(function(){
		  $(this).appendDtpicker({
			"autodateOnStart": false
		  });
	  });

  });



