function save_rpt(){
 //검색조건값을 초기 셋팅합니다. 
         alert('저장합니다.');
         $.ajax({
             cache: false,
             type: "POST",
             url: "/rpt_write_act.ado?s_no=${s_no}$",
             data: $("#rpt_frm").serialize(),
             dataType: 'json',
             success: function(json) {
              alert(json.msg);
             },
             error: function(jqXHR, textStatus, errorThrown) {
               console.error('ERRORS: ' + textStatus);
               alert('ERRORS: ' + textStatus);
             }
        });
}

function submit_rpt(){
         alert('저장합니다.');
         $.ajax({
             cache: false,
             type: "POST",
             url: "/rpt_write_act.ado?s_no=${s_no}$&status=complete",
             data: $("#rpt_frm").serialize(),
             dataType: 'json',
             success: function(json) {
              alert(json.msg);

              $('#rpt_frm').attr('action', 'actual_report.do').submit();
             },
             error: function(jqXHR, textStatus, errorThrown) {
               console.error('ERRORS: ' + textStatus);
               alert('ERRORS: ' + textStatus);
             }
         });
}

  $(document).ready(function(){

	  		${if(isAccess!='')}$
	  	  	self.location.href="${isAccess}$";
	  	  	return;
	  	  ${endif}$	
      //파일 업로드를 합니다. 
      $('#file_upload').on('click', function(){
        alert('파일을 업로드합니다.');

        var ids_arr = "";

        //$('input:file[name="file"]').each(function() {
        $('input:file').each(function() {
            if(this.value!=''){
              ids_arr +=$(this).attr('ids')+"|";
            }
        });

        $('#file_frm').attr("target","ifrm");
        $('#file_frm').attr("action", "/file_upload.do?s_no=${s_no}$&ids_arr="+ids_arr);
        $('#file_frm').submit();
      });

      //수정화면이거나 완료 화면인경우 파일다운로드 링크구현
     ${forEach:fileLst}$
     var src_val ="/view_file.do?s_no=${fileLst.s_no}$&seq=${fileLst.seq}$";     
     ${if(fileLst.is_img==IMG)}$
        $('#f${fileLst.seq}$').html("<a href='"+src_val+"'><img width=100 height=100 src='"+src_val+"'></a>");
     ${else}$
        $('#f${fileLst.seq}$').html("<a href='"+src_val+"'>${fileLst.org_file_name}$</a>");
     ${endif}$
     ${endfor}$

      //조회화면 일경우 화면 구현
     ${if(typ==inq_rpt)}$
     		//관리자경우
     	${if(auth!=c)}$
     	    $('#alert_rpt').hide();
	       	$('#rpt_info_table').show();
	        $('#div_databox').removeClass();
	        $('#content_id').removeClass();
	    ${else}$
	    	$('#alert_rpt').show();

        ${endif}$
	    	//완료보고서 수정불가하도록. 
	    	  $('input').each(function() {
	    	  	this.disabled=true;
	    	  });
	    	  $('select').each(function() {
	    	  	this.disabled=true;
	    	  });
	    	  $('textarea').each(function() {
	    	  	this.disabled=true;
	    	  });
        $('#divSaveBtn').hide();
        $('#nav_mnu').hide();
        $('#upload_btn_div').hide();
     ${endif}$ 

      //파일업로드를 위한 설정
      $('input[type=file]').bootstrapFileInput();
      $('.file-inputs').bootstrapFileInput();

      ${if(s_no='')}$
        alert('현재 정해진 리포트 넘버가 없습니다. 목록 페이지로 이동합니다.');
        history.back(-1); //뒤로넘김
      ${endif}$

     ${forEach:itemLst}$
      //라디오 버튼인경우 처리
      if($('input[name="${itemLst.item_cd}$"]').attr('type')=='radio') {
		    $('input:radio[name="${itemLst.item_cd}$"]').each(function() {
		        if(this.value=='${itemLst.val_char}$'){ 
		            this.checked = true; 
		        }
		    });

      //아래는 checkbox인경우의 처리
      }else if($('input[name="${itemLst.item_cd}$"]').attr('type')=='checkbox') {
        //alert('${itemLst.item_cd}$');
        //값을 루핑하면서 check 처리를 해준다.
        var chkVal = '${itemLst.val_char}$' ;
        var chkValArr = chkVal.replaceAll("\\[","").replaceAll("\\]","").split(",");
        $('input:checkbox[name="${itemLst.item_cd}$"]').each(function() {
		        for(k=0; k < chkValArr.length ; k++){
		           if(this.value==chkValArr[k].trim()){
                 //alert(chkValArr[k]);
                 this.checked = true;
              }
		        }
        });

      }else{  //radio 버튼이 아닌 경우
	      //alert('${itemLst.item_cd}$');
	      //alert( $('input[name="${itemLst.item_cd}$"]').attr('type'));
        $('#${itemLst.item_cd}$').val('${itemLst.val_char}$');
      }
     ${endfor}$

     ${forEach:opnLst}$
       var txt_ = $('#${opnLst.item_cd}$_hidden').val(); 
       $('#${opnLst.item_cd}$').val(txt_);
     ${endfor}$


     //기술역량의 해당사항없을 시, 다른 체크박스 비활성화 
     $('#D04_none').change(function(){
     	if($('#D04_none').prop("checked") == true){
     		$('input:checkbox[id="D04"]').each(function() {
     			this.checked = false;
     			this.disabled = true;
     		});
     	} else{
     		$('input:checkbox[id="D04"]').each(function() {
     			this.disabled = false;
     		});
     	}
     });

     $('input:radio[name=E09]').change(function(){
     //외주생산여부가 없는경우 입력비활성화
     	if($('input:radio[name=E09]:checked').val() == 0){
     		$('#out_prop').prop('disabled', true);
     		$('input[name=E08]').attr("checked",false);
     		$('input[name=E08]').attr("disabled",true);
     	} else{
     		$('input[name=E08]').attr("disabled",false);
     		$('#out_prop').prop('disabled', false);
     	}
     });

     $('.tcb_number').tcbNumberOnly();

  });




